package com.cg.dayone;

public class Typecasting {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=20;
		System.out.println(a);
		float b=a;
		System.out.println(b);
		double c=26.89;
		System.out.println(c);
		int d=(int)c;
		System.out.println(d);

	}

}
